import React, { useState } from "react";
import { AppProvider } from "@toolpad/core/AppProvider";
import { DashboardLayout } from "@toolpad/core/DashboardLayout";
import { Box, Tab, Tabs } from "@mui/material";
import ApiTwoToneIcon from '@mui/icons-material/ApiTwoTone';
import { IconPositionTabs } from './RequestCard';

// Modify the navigation array to track the disabled tabs (grayed out)
const NAVIGATION = [
  { segment: "RTC", title: "RTC", icon: <AccountBalanceTwoToneIcon /> },
  { segment: "EDSDeP", title: "EDSDeP", icon: <ApiTwoToneIcon />, disabled: false },
  { segment: "SOSA", title: "SOSA", icon: <ScienceTwoToneIcon /> },
  { segment: "DDS", title: "DDS", icon: <DeviceHubIcon /> },
  { segment: "LOPR", title: "LOPR", icon: <DashboardIcon /> },
  { segment: "KW", title: "Kafka Watcher", icon: <MonitorHeartIcon /> }
];

export default function DashboardLayoutBasic(props) {
  const { window } = props;
  const [loading, setLoading] = useState(true);
  const [disableEDSDeP, setDisableEDSDeP] = useState(true);  // Example state to disable "EDSDeP"

  const router = useDemoRouter("/RTC");
  const demoWindow = window ? window() : undefined;

  return (
    <AppProvider
      branding={{ title: 'uDeploy' }}
      navigation={NAVIGATION}
      router={router}
      theme={demoTheme}
      window={demoWindow}
    >
      <DashboardLayout>
        {router.pathname === "/RTC" && (
          <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", p: 1 }}>
            <Tabs value={router.pathname} aria-label="navigation tabs">
              {NAVIGATION.map((navItem) => (
                <Tab
                  key={navItem.segment}
                  label={navItem.title}
                  value={navItem.segment}
                  icon={navItem.icon}
                  sx={{
                    color: disableEDSDeP && navItem.segment === "EDSDeP" ? "gray" : "inherit",  // Grays out the "EDSDeP" tab
                    opacity: disableEDSDeP && navItem.segment === "EDSDeP" ? 0.5 : 1,  // Reduces opacity
                    pointerEvents: disableEDSDeP && navItem.segment === "EDSDeP" ? "none" : "auto",  // Prevents clicking
                  }}
                />
              ))}
            </Tabs>
          </Box>
        )}
      </DashboardLayout>
    </AppProvider>
  );
}
