import React, { useState, useEffect } from "react";
import { Box, Paper } from "@mui/material";
import ApiTwoToneIcon from '@mui/icons-material/ApiTwoTone';
import { AppProvider } from "@toolpad/core/AppProvider";
import { DashboardLayout } from "@toolpad/core/DashboardLayout";
import { IconPositionTabs } from './RequestCard';

const NAVIGATION = [
  { kind: "header", title: "Clearing" },
  { segment: "RTC", title: "RTC", icon: <AccountBalanceTwoToneIcon /> },
  { kind: "divider" },
  { segment: "EDSDeP", title: "EDSDeP", icon: <ApiTwoToneIcon />, disabled: false },
  { kind: "divider" },
  { segment: "SOSA", title: "SOSA", icon: <ScienceTwoToneIcon /> },
  { kind: "divider" },
  { segment: "DDS", title: "DDS", icon: <DeviceHubIcon /> },
  { kind: "divider" },
  { segment: "LOPR", title: "LOPR", icon: <DashboardIcon /> },
  { kind: "divider" },
  { segment: "KW", title: "Kafka Watcher", icon: <MonitorHeartIcon /> },
  { kind: "divider" },
  { kind: "header", title: "Environments" },
  {
    segment: "NCZ",
    title: "NCZ",
    icon: <BarChartIcon />,
    children: [
      { segment: "k8s-dev-01-clrg", title: "k8s-dev-01-clrg", icon: <DescriptionIcon /> },
      { segment: "k8s-dev-01-shared", title: "k8s-dev-01-shared", icon: <DescriptionIcon /> }
    ]
  },
  {
    segment: "DPZ",
    title: "DPZ",
    icon: <LockIcon />,
    children: [
      { segment: "k8s-dev-01-clearing", title: "k8s-dev-01-clearing", icon: <DescriptionIcon /> },
      { segment: "k8s-rat-01-shared", title: "k8s-rat-01-shared", icon: <DescriptionIcon /> }
    ]
  },
];

export default function DashboardLayoutBasic(props) {
  const { window } = props;
  const [loading, setLoading] = useState(true);
  const [disableEDSDeP, setDisableEDSDeP] = useState(false);

  // Simulate receiving a Kafka message
  useEffect(() => {
    const socket = new WebSocket("ws://your-kafka-url"); // Replace with actual Kafka URL
    socket.onmessage = (event) => {
      const message = JSON.parse(event.data);
      if (message.action === "disable-EDSDeP") {
        setDisableEDSDeP(true);
      }
    };

    return () => socket.close();
  }, []);

  // Dynamically update the navigation array with the disabled state
  const navigationWithDisabledTab = NAVIGATION.map(item =>
    item.segment === "EDSDeP" ? { ...item, disabled: disableEDSDeP } : item
  );

  const router = useDemoRouter("/RTC");
  const demoWindow = window ? window() : undefined;

  return (
    <AppProvider
      branding={{ title: 'uDeploy' }}
      navigation={navigationWithDisabledTab}
      router={router}
      theme={demoTheme}
      window={demoWindow}
    >
      <DashboardLayout>
        {router.pathname === "/RTC" && (
          <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", p: 1 }}>
            <Paper sx={{ p: 3 }}>
              <IconPositionTabs />
            </Paper>
          </Box>
        )}
      </DashboardLayout>
    </AppProvider>
  );
}
